-- Quran bookmarks and notes
CREATE TABLE IF NOT EXISTS quran_bookmarks (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  surah_number INTEGER NOT NULL,
  ayah_number INTEGER NOT NULL,
  note TEXT,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  UNIQUE(user_id, surah_number, ayah_number)
);

ALTER TABLE quran_bookmarks ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Users can view their own bookmarks" ON quran_bookmarks
  FOR SELECT USING (auth.uid() = user_id);

CREATE POLICY "Users can insert their own bookmarks" ON quran_bookmarks
  FOR INSERT WITH CHECK (auth.uid() = user_id);

CREATE POLICY "Users can update their own bookmarks" ON quran_bookmarks
  FOR UPDATE USING (auth.uid() = user_id);

CREATE POLICY "Users can delete their own bookmarks" ON quran_bookmarks
  FOR DELETE USING (auth.uid() = user_id);

-- Quran memorization tracking
CREATE TABLE IF NOT EXISTS quran_memorization (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  surah_number INTEGER NOT NULL,
  ayah_from INTEGER NOT NULL,
  ayah_to INTEGER NOT NULL,
  memorization_level VARCHAR(50) DEFAULT 'learning',
  last_reviewed TIMESTAMPTZ,
  review_count INTEGER DEFAULT 0,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

ALTER TABLE quran_memorization ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Users can manage their own memorization" ON quran_memorization
  FOR ALL USING (auth.uid() = user_id);

-- Hadith collections
CREATE TABLE IF NOT EXISTS hadiths (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  collection VARCHAR(100) NOT NULL,
  book VARCHAR(100),
  hadith_number VARCHAR(50),
  arabic_text TEXT NOT NULL,
  translation TEXT NOT NULL,
  narrator VARCHAR(200),
  grade VARCHAR(50),
  audio_url TEXT,
  category VARCHAR(100),
  created_at TIMESTAMPTZ DEFAULT NOW()
);

ALTER TABLE hadiths ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Anyone can view hadiths" ON hadiths
  FOR SELECT USING (true);

-- User hadith favorites
CREATE TABLE IF NOT EXISTS user_hadith_favorites (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  hadith_id UUID REFERENCES hadiths(id) ON DELETE CASCADE,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  UNIQUE(user_id, hadith_id)
);

ALTER TABLE user_hadith_favorites ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Users can manage their own favorites" ON user_hadith_favorites
  FOR ALL USING (auth.uid() = user_id);

-- Learning quizzes
CREATE TABLE IF NOT EXISTS learning_quizzes (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  title VARCHAR(200) NOT NULL,
  description TEXT,
  category VARCHAR(100) NOT NULL,
  difficulty VARCHAR(50) DEFAULT 'beginner',
  questions JSONB NOT NULL,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

ALTER TABLE learning_quizzes ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Anyone can view quizzes" ON learning_quizzes
  FOR SELECT USING (true);

-- User quiz progress
CREATE TABLE IF NOT EXISTS user_quiz_progress (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  user_id UUID REFERENCES auth.users(id) ON DELETE CASCADE,
  quiz_id UUID REFERENCES learning_quizzes(id) ON DELETE CASCADE,
  score INTEGER NOT NULL,
  total_questions INTEGER NOT NULL,
  completed_at TIMESTAMPTZ DEFAULT NOW(),
  time_taken_seconds INTEGER
);

ALTER TABLE user_quiz_progress ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Users can view their own quiz progress" ON user_quiz_progress
  FOR SELECT USING (auth.uid() = user_id);

CREATE POLICY "Users can insert their own quiz progress" ON user_quiz_progress
  FOR INSERT WITH CHECK (auth.uid() = user_id);

-- 99 Names of Allah
CREATE TABLE IF NOT EXISTS allah_names (
  id UUID PRIMARY KEY DEFAULT extensions.uuid_generate_v4(),
  number INTEGER UNIQUE NOT NULL,
  arabic VARCHAR(100) NOT NULL,
  transliteration VARCHAR(100) NOT NULL,
  translation VARCHAR(200) NOT NULL,
  meaning TEXT NOT NULL,
  benefits TEXT,
  audio_url TEXT,
  created_at TIMESTAMPTZ DEFAULT NOW()
);

ALTER TABLE allah_names ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Anyone can view Allah names" ON allah_names
  FOR SELECT USING (true);

