import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.39.3';

const GOOGLE_AI_API_KEY = Deno.env.get('GOOGLE_AI_API_KEY');
const SUPABASE_URL = Deno.env.get('SUPABASE_URL')!;
const SUPABASE_SERVICE_ROLE_KEY = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!;

interface AIRequest {
  type: 'quote' | 'hadith' | 'question' | 'advice' | 'prayer_reflection' | 'dua' | 'quiz' | 'ramadan';
  payload?: any;
}

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    // Get the authorization header
    const authHeader = req.headers.get('Authorization');
    if (!authHeader) {
      return new Response(
        JSON.stringify({ error: 'Missing authorization header' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Create Supabase client
    const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY);

    // Get user from JWT
    const token = authHeader.replace('Bearer ', '');
    const { data: { user }, error: userError } = await supabase.auth.getUser(token);

    if (userError || !user) {
      return new Response(
        JSON.stringify({ error: 'Invalid authorization token' }),
        { status: 401, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check user subscription and feature access
    const { data: subscription } = await supabase
      .from('user_subscriptions')
      .select('*, subscription_tiers(name)')
      .eq('user_id', user.id)
      .eq('status', 'active')
      .single();

    const userTier = subscription?.subscription_tiers?.name || 'free';

    // Parse request body
    const { type, payload }: AIRequest = await req.json();

    // Check feature access based on type
    const featureRequirements: Record<string, string> = {
      'quote': 'free',
      'hadith': 'premium',
      'question': 'ultra',
      'advice': 'ultra',
      'prayer_reflection': 'premium',
      'dua': 'premium',
      'quiz': 'ultra',
      'ramadan': 'premium',
    };

    const requiredTier = featureRequirements[type] || 'premium';
    const tierHierarchy = ['free', 'premium', 'ultra'];
    const userTierIndex = tierHierarchy.indexOf(userTier);
    const requiredTierIndex = tierHierarchy.indexOf(requiredTier);

    if (userTierIndex < requiredTierIndex) {
      return new Response(
        JSON.stringify({ 
          error: 'Premium feature', 
          message: `This feature requires ${requiredTier} subscription`,
          required_tier: requiredTier,
          current_tier: userTier
        }),
        { status: 403, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Check if API key is configured
    if (!GOOGLE_AI_API_KEY) {
      return new Response(
        JSON.stringify({ error: 'AI service not configured' }),
        { status: 503, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      );
    }

    // Call Google Gemini API based on type
    let result;
    switch (type) {
      case 'quote':
        result = await generateQuote(payload?.topic || 'faith and spirituality');
        break;
      case 'hadith':
        result = await generateHadith();
        break;
      case 'question':
        result = await answerQuestion(payload?.question);
        break;
      case 'advice':
        result = await generateAdvice(payload?.context);
        break;
      case 'prayer_reflection':
        result = await generatePrayerReflection(payload?.prayerName);
        break;
      case 'dua':
        result = await generateDua(payload?.need);
        break;
      case 'quiz':
        result = await generateQuiz(payload?.topic, payload?.difficulty);
        break;
      case 'ramadan':
        result = await generateRamadanContent(payload?.type);
        break;
      default:
        return new Response(
          JSON.stringify({ error: 'Invalid request type' }),
          { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
        );
    }

    return new Response(
      JSON.stringify(result),
      { status: 200, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: error.message || 'Internal server error' }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );
  }
});

async function callGemini(prompt: string): Promise<string> {
  const response = await fetch(
    `https://generativelanguage.googleapis.com/v1beta/models/gemini-pro:generateContent?key=${GOOGLE_AI_API_KEY}`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        contents: [{ parts: [{ text: prompt }] }],
      }),
    }
  );

  if (!response.ok) {
    throw new Error(`Gemini API error: ${response.statusText}`);
  }

  const data = await response.json();
  return data.candidates[0]?.content?.parts[0]?.text || '';
}

async function generateQuote(topic: string) {
  const prompt = `Generate an inspiring Quranic verse about ${topic}. 

    Provide the response in the following JSON format:
    {
      "arabic": "The verse in Arabic",
      "translation": "English translation",
      "reference": "Surah Name Chapter:Verse",
      "context": "Brief historical or situational context (2-3 sentences)",
      "reflection": "Personal reflection on how this applies to modern life (2-3 sentences)"
    }

    Make sure the verse is authentic and the reference is accurate.`;

  const text = await callGemini(prompt);
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    return JSON.parse(jsonMatch[0]);
  }
  throw new Error('Failed to parse response');
}

async function generateHadith() {
  const prompt = `Generate an authentic Hadith (saying of Prophet Muhammad ﷺ) for daily reflection.

    Provide the response in the following JSON format:
    {
      "hadith": "The Hadith text in English",
      "reference": "Collection name (e.g., Sahih Bukhari, Sahih Muslim) and number",
      "explanation": "Brief explanation of the Hadith's meaning and relevance (3-4 sentences)"
    }`;

  const text = await callGemini(prompt);
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    return JSON.parse(jsonMatch[0]);
  }
  throw new Error('Failed to parse response');
}

async function answerQuestion(question: string) {
  const prompt = `You are a knowledgeable Islamic scholar assistant. Answer the following question about Islam with accuracy, respect, and clarity. Base your answers on authentic Islamic sources (Quran and Hadith).

Question: ${question}

Provide a clear, respectful, and informative answer. Include relevant Quranic verses or Hadith references when applicable. Format your response as follows:

ANSWER:
[Your detailed answer here]

REFERENCES:
- [Reference 1]
- [Reference 2]
(Include only if applicable)`;

  const text = await callGemini(prompt);

  const answerMatch = text.match(/ANSWER:\s*([\s\S]*?)(?=REFERENCES:|$)/);
  const referencesMatch = text.match(/REFERENCES:\s*([\s\S]*?)$/);

  const answer = answerMatch ? answerMatch[1].trim() : text;
  const references: string[] = [];

  if (referencesMatch) {
    const refText = referencesMatch[1];
    const refLines = refText.split('\n').filter(line => line.trim().startsWith('-'));
    references.push(...refLines.map(line => line.replace(/^-\s*/, '').trim()));
  }

  return { answer, references: references.length > 0 ? references : undefined };
}

async function generateAdvice(context: string) {
  const prompt = `As a compassionate Islamic advisor, provide spiritual guidance for someone experiencing: ${context}

Offer advice based on Islamic teachings, including relevant Quranic verses or Hadith. Be empathetic, practical, and encouraging. Keep your response concise but meaningful (3-4 paragraphs).`;

  return { advice: await callGemini(prompt) };
}

async function generatePrayerReflection(prayerName: string) {
  const prompt = `Generate a brief, inspiring reflection about ${prayerName} prayer in Islam. 

Include:
- The significance of this prayer
- A relevant Quranic verse or Hadith
- A practical tip for improving focus during this prayer

Keep it concise (3-4 sentences) and uplifting.`;

  return { reflection: await callGemini(prompt) };
}

async function generateDua(need: string) {
  const prompt = `Generate a personalized Islamic dua (supplication) for someone seeking: ${need}

Provide:
1. A dua in English
2. Brief explanation of its significance
3. Relevant Quranic verse or Hadith if applicable

Keep it authentic to Islamic teachings and respectful.`;

  return { dua: await callGemini(prompt) };
}

async function generateQuiz(topic: string, difficulty: string) {
  const prompt = `Generate a ${difficulty} level Islamic quiz about ${topic}.

Provide 5 multiple-choice questions in the following JSON format:
{
  "questions": [
    {
      "question": "Question text",
      "options": ["Option A", "Option B", "Option C", "Option D"],
      "correctAnswer": 0,
      "explanation": "Brief explanation of the correct answer"
    }
  ]
}

Make sure questions are accurate, educational, and appropriate for the difficulty level.`;

  const text = await callGemini(prompt);
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    return JSON.parse(jsonMatch[0]);
  }
  throw new Error('Failed to parse response');
}

async function generateRamadanContent(type: string) {
  let prompt = '';
  switch (type) {
    case 'suhoor':
      prompt = 'Provide a brief, inspiring message for Suhoor (pre-dawn meal) during Ramadan. Include a relevant dua and practical tips. Keep it concise (2-3 paragraphs).';
      break;
    case 'iftar':
      prompt = 'Provide a brief, inspiring message for Iftar (breaking fast) during Ramadan. Include the traditional dua and a reflection. Keep it concise (2-3 paragraphs).';
      break;
    case 'taraweeh':
      prompt = 'Provide a brief, inspiring message about Taraweeh prayers during Ramadan. Include benefits and encouragement. Keep it concise (2-3 paragraphs).';
      break;
  }

  return { content: await callGemini(prompt) };
}
